mapper.select:
select id as id, company_name as companyName, company_tip as companyTip, company_type as companyType, join_price as joinPrice, application_count as applicationCount, good_evaluate as goodEvaluate, shop_count as shopCount, big_pic_url as bigPicUrl, small_pic_url as smallPicUrl, company_profile as companyProfile from company_info
and company_name like #{companyName}
and company_type = #{companyType}
and big_pic_url = #{bigPicUrl}
and small_pic_url = #{smallPicUrl}
and join_price <= maxPrice
and join_price >= minPrice

select id as id, company_name as companyName, company_tel as companyTel, company_tip as companyTip, company_type as companyType, join_price as joinPrice, application_count as applicationCount, good_evaluate as goodEvaluate, shop_count as shopCount, big_pic_url as bigPicUrl, small_pic_url as smallPicUrl, company_profile as companyProfile from company_info
and id = #{id}
and big_pic_url = #{bigPicUrl}
and small_pic_url = #{smallPicUrl}
